var ib;
var archiverTopic = flow.get('archiverTopic');

var photoDetValueView       = new DataView(new ArrayBuffer(4));
var ultraDistanceView       = new DataView(new ArrayBuffer(4));
var ultraAlarmDistanceView  = new DataView(new ArrayBuffer(4));
var tempAvgView             = new DataView(new ArrayBuffer(4));
var ledStateView            = new DataView(new ArrayBuffer(4));
var ledValueView            = new DataView(new ArrayBuffer(4));
var ledPeriodView           = new DataView(new ArrayBuffer(4));

for (ib = 0; ib < 4; ++ib)      photoDetValueView.setUint8(ib, msg.payload[0 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)      ultraDistanceView.setUint8(ib, msg.payload[1 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib) ultraAlarmDistanceView.setUint8(ib, msg.payload[2 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)            tempAvgView.setUint8(ib, msg.payload[3 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)           ledStateView.setUint8(ib, msg.payload[4 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)           ledValueView.setUint8(ib, msg.payload[5 * 4 + 3 - ib]);
for (ib = 0; ib < 4; ++ib)          ledPeriodView.setUint8(ib, msg.payload[6 * 4 + 3 - ib]);

var photoDetValueMsg = 
{
    topic: flow.get('archiverTopic') + 'photoDetValue/reading',
    payload : 
    {   
        type :"scalar",
        value:photoDetValueView.getInt32(0)
    }
};
var ultraDistanceMsg = 
{
    topic: flow.get('archiverTopic') + 'ultraDistance/reading',
    payload : 
    {   
        type :"scalar",
        value:ultraDistanceView.getInt32(0)
    }
};
var ultraAlarmDistanceMsg = 
{
    topic: flow.get('archiverTopic') + 'ultraAlarmDistance/reading',
    payload : 
    {   
        type :"scalar",
        value:ultraAlarmDistanceView.getInt32(0)
    }
};
var tempAvgMsg = 
{
    topic: flow.get('archiverTopic') + 'tempAvg/reading',
    payload : 
    {   
        type :"scalar",
        value:Math.round(100.0 * tempAvgView.getFloat32(0)) / 100.0
    }
};
var ledStateMsg = 
{
    topic: flow.get('archiverTopic') + 'ledState/reading',
    payload : 
    {   
        type :"scalar",
        value:ledStateView.getInt32(0)
    }
};
var ledValueMsg = 
{
    topic: flow.get('archiverTopic') + 'ledValue/reading',
    payload : 
    {   
        type :"scalar",
        value:ledValueView.getInt32(0)
    }
};
var ledPeriodMsg = 
{
    topic: flow.get('archiverTopic') + 'ledPeriod/reading',
    payload : 
    {   
        type :"scalar",
        value:ledPeriodView.getInt32(0)
    }
};
return [
    photoDetValueMsg, 
    ultraDistanceMsg, 
    ultraAlarmDistanceMsg, 
    tempAvgMsg, 
    ledStateMsg, 
    ledValueMsg, 
    ledPeriodMsg];

var echoSettings = 0;
var ledState = 0;
var ledValue = 0;
var ledPeriod = 0;
var ultraAlarmDistance = 0;

var echoSettingsMask = 0;
var ledStateMask = 0;
var ledValueMask = 0;
var ledPeriodMask = 0;
var ultraAlarmDistanceMask = 0;

switch(msg.topic) 
{
  case 'echoSettings':
    echoSettings = msg.payload;
    echoSettingsMask = 1;
    break;
  case 'ledState':
    ledState = msg.payload;
    ledStateMask = 1;
    break;
  case 'ledValue':
    ledValue = msg.payload;
    ledValueMask = 1;
    break;
  case 'ledPeriod':
    ledPeriod = msg.payload;
    ledPeriodMask = 1;
    break;
  case 'ultraAlarmDistance':
    ultraAlarmDistance = msg.payload;
    ultraAlarmDistanceMask = 1;
    break;
  default:
}

var buffer = new ArrayBuffer(40);
var settingsView = new DataView(buffer);
settingsView.setInt32(0,echoSettingsMask, true);
settingsView.setInt32(4,ledStateMask, true);
settingsView.setInt32(8,ledValueMask, true);
settingsView.setInt32(12,ledPeriodMask, true);
settingsView.setInt32(16,ultraAlarmDistanceMask, true);

settingsView.setInt32(20,echoSettings, true);
settingsView.setInt32(24,ledState, true);
settingsView.setInt32(28,ledValue, true);
settingsView.setInt32(32,ledPeriod, true);
settingsView.setInt32(36,ultraAlarmDistance, true);

return {topic:'settings',payload:Buffer.from(buffer)};

var archiverTopic = flow.get('archiverTopic').split('/');
var inputTopic =  msg.topic.split('/');
for (var ii = 0; ii < 6; ++ii)
{
    if (archiverTopic[ii] != inputTopic[ii]) return null;
}
var deviceAttr = inputTopic[6]
return {topic: deviceAttr, payload:msg.payload.value};
